{"script": "#!/bin/bash

# Find the total number of files in all subdirectories of test_directory

total_files=0

for subdir in *;
do { 
  # Get the number of files in each subdirectory using ls -l | wc -l
  total_files=$((total_files + $(ls -l $subdir | wc -l))) 
}

echo "Total number of files: $total_files"
"}

