function.arguments:
{"script": "folders=$(find test_directory -type d -mindepth 1 | wc -l);
chars=$(find test_directory -type f -name "*.txt" -exec wc -c {} + | awk '{sum += $1} END {print sum}');
words=$(find test_directory -type f -name "*.txt" -exec wc -w {} + | awk '{sum += $1} END {print sum}');
lines=$(find test_directory -type f -name "*.txt" -exec wc -l {} + | awk '{sum += $1} END {print sum}');
total=$((chars + words + lines + folders));
echo $total"}

{"script": "count1=$(find . -mindepth 1 -maxdepth 1 -type f -o -type d | wc -l);
count2=$(find . -type f -name "*.txt" -size +0 | wc -l);
result=$((count1 * count2));
echo $result"}

{"script": "count_files_dirs=$(find test -mindepth 1 -maxdepth 1 ! -name ".*" | wc -l);
count_txt_files=$(find test -name "*.txt" -type f -size +0 | wc -l);
result=$((count_files_dirs * count_txt_files));
echo $result"}

{"script": "count_dirs=$(find exp_folder -type d | wc -l);
count_files=$(find exp_folder -type f | wc -l);
total=$((count_dirs + count_files));
echo $total"}

{"script": "directories=$(find problem_directory -type d -mindepth 1 | wc -l);
